<?phpnamespace App\Models;use Jenssegers\Mongodb\Eloquent\Model as Eloquent;class TMenu extends Eloquent{	protected $connection = 'mongodb';	protected $collection = 't_menus';	public function submenus() {		return $this->hasMany('App\Models\TSubMenu');    }	public static $rules = array(        'menu_name' => 'required',        'menu_order' => 'required',    );    public static $messages = array(        'menu_name.required' => 'Please Add Menu Name',        'menu_order.required' => 'Give The Order Number',    );}